import osimport cv2img_root = 'E:\srtp1\object_detection_projects-master\opencv_traffic_counting\out\processed_'#这里写你的文件夹路径，比如：/home/youname/data/img/,注意最后一个文件夹要有斜杠fps = 20    #保存视频的FPS，可以适当调整#可以用(*'DVIX')或(*'X264'),如果都不行先装ffmepg: sudo apt-get install ffmepgfourcc = cv2.VideoWriter_fourcc(*'x264')videoWriter = cv2.VideoWriter('saveVideo.avi',-1,fps,(720, 1280))#最后一个是保存图片的尺寸for i in range(750):    str1=img_root+str(i)+'.png'    #print(str1)    frame = cv2.imread(str1)    cv2.namedWindow('image1', 0)    cv2.imshow('image1',frame)    cv2.waitKey(27)    videoWriter.write(frame)    print(img_root+str(i)+'.png')videoWriter.release()